INSERT INTO `courses` (`id`, `string_id`, `name_en`, `name_fr`, `url_en`, `url_fr`, `description`, `avg_overall_grade`, `avg_exercises_grade`, `avg_lectures_grade`, `avg_content_grade`, `avg_difficulty`, `teacher_id`, `section_id`) VALUES
(1, 'PHYS-115', 'Building physics I', 'Physique du bâtiment I', 'http://edu.epfl.ch/coursebook/en/building-physics-i-PHYS-115', 'http://edu.epfl.ch/coursebook/fr/physique-du-batiment-i-PHYS-115', 'Ce cours traite des principaux phénomènes physiques observables dans le bâtiment et doit permettre à l''étudiant d''acquérir des connaissances de base dans le domaine.', NULL, NULL, NULL, NULL, NULL, NULL, 30, 1),
(2, 'PHYS-116', 'Building physics II', 'Physique du bâtiment II', 'http://edu.epfl.ch/coursebook/en/building-physics-ii-PHYS-116', 'http://edu.epfl.ch/coursebook/fr/physique-du-batiment-ii-PHYS-116', 'Ce cours fournit une introduction aux principes gouvernants les phénomènes du rayonnement thermique, de la lumière, de la couleur, et du son, focalisant particulièrement sur les applications dans le domaine du bâtiment.', NULL, NULL, NULL, NULL, NULL, NULL, 31, 1),
(3, 'MATH-124', 'Geometry for architects I', 'Géométrie pour architectes I', 'http://edu.epfl.ch/coursebook/en/geometry-for-architects-i-MATH-124', 'http://edu.epfl.ch/coursebook/fr/geometrie-pour-architectes-i-MATH-124', 'Ce cours entend exposer les fondements de la géométrie à un triple titre :\n1/ de technique mathématique essentielle au processus de conception du projet,\n2/ d''objet privilégié des logiciels de conception paramétrique \n3/ de composante à part entière de l''histoire de la culture architecturale.', NULL, NULL, NULL, NULL, NULL, NULL, 32, 1),
(4, 'MATH-126', 'Geometry for architects II', 'Géométrie pour architectes II', 'http://edu.epfl.ch/coursebook/en/geometry-for-architects-ii-MATH-126', 'http://edu.epfl.ch/coursebook/fr/geometrie-pour-architectes-ii-MATH-126', 'Ce cours traite des 3 sujets suivants par une approche géométrique: la perspective (par la géométrie descriptive), les coniques (par leurs différentes modes de représentation), et la géométrie vectorielle du plan et de l''espace.', NULL, NULL, NULL, NULL, NULL, NULL, 32, 1),
(5, 'AR-123', 'History of architecture I', 'Histoire de l''architecture I', 'http://edu.epfl.ch/coursebook/en/history-of-architecture-i-AR-123', 'http://edu.epfl.ch/coursebook/fr/histoire-de-l-architecture-i-AR-123', 'L''architecture contemporaine est héritière de quelque 5000 ans d''une accumulation d''expériences, dont la genèse se situe dans l''Orient Méditerranéen. Compte tenu de la masse d''informations dont dispose l''architecte d''aujourd''hui, il est impératif qu''il en connaisse les sources.', NULL, NULL, NULL, NULL, NULL, NULL, 33, 1),
(6, 'AR-124', 'History of architecture II', 'Histoire de l''architecture II', 'http://edu.epfl.ch/coursebook/en/history-of-architecture-ii-AR-124', 'http://edu.epfl.ch/coursebook/fr/histoire-de-l-architecture-ii-AR-124', 'Le cours se propose de fournir les instruments critiques pour une connaissance approfondie des principales thématiques qui caractérisent l''architecture du Quattrocento en Italie.', NULL, NULL, NULL, NULL, NULL, 34, 1),
(7, 'MATH-184', 'Mathematics I', 'Mathématiques I', 'http://edu.epfl.ch/coursebook/en/mathematics-i-MATH-184', 'http://edu.epfl.ch/coursebook/fr/mathematiques-i-MATH-184', 'Ce cours présente les objets et outils de base de l''analyse du point de vue infinitésimal:  polynômes, exponentielles, logarithmes, fonctions trigonométriques, séries de Taylor, différentiation et intégration.', NULL, NULL, NULL, NULL, NULL, 35, 7),
(8, 'MATH-186', 'Mathematics II', 'Mathématiques II', 'http://edu.epfl.ch/coursebook/en/mathematics-ii-MATH-186', 'http://edu.epfl.ch/coursebook/fr/mathematiques-ii-MATH-186', 'Ce cours introduit les techniques analytiques nécessaires pour représenter les courbes et surfaces en relation avec l''architecture - et sous-jacentes aux outils informatiques de représentation bi- et tridimensionnelle.', NULL, NULL, NULL, NULL, NULL, 35, 7),
(9, 'AR-131', 'Construction and sustainability I', 'Construction et durabilité I', 'http://edu.epfl.ch/coursebook/en/construction-and-sustainability-i-AR-131', 'http://edu.epfl.ch/coursebook/fr/construction-et-durabilite-i-AR-131', 'Introduction aux principaux éléments de construction: structure porteuse, enveloppe, ouvertures, équipements et installations, matériaux de construction correspondants, ainsi qu''aux méthodes et techniques d''assemblage de ces éléments dans un ensemble construit cohérent, performant et durable.', NULL, NULL, NULL, NULL, NULL, 36, 1),
(10, 'AR-132', 'Construction and sustainability II', 'Construction et durabilité II', 'http://edu.epfl.ch/coursebook/en/construction-and-sustainability-ii-AR-132', 'http://edu.epfl.ch/coursebook/fr/construction-et-durabilite-ii-AR-132', 'Introduction aux principaux éléments de construction: structure porteuse, enveloppe, ouvertures, équipements et installations, matériaux de construction correspondants, ainsi qu''aux méthodes et techniques d''assemblage de ces éléments dans un ensemble construit cohérent, performant et durable.', NULL, NULL, NULL, NULL, NULL, 36, 1),
(11, 'AR-151', 'Elements of urban and territorial analysis I', 'Eléments d''analyses urbaines et territoriales I', 'http://edu.epfl.ch/coursebook/en/elements-of-urban-and-territorial-analysis-i-AR-151', 'http://edu.epfl.ch/coursebook/fr/elements-d-analyses-urbaines-et-territoriales-i-AR-151', 'Ce cours donnera aux étudiants les bases nécessaires pour identifier les éléments principaux des structures urbaines et territoriales, pour en comprendre les conditions d''émergence, leurs significations multiples et les possibilités d''en faire les supports de projets.', NULL, NULL, NULL, NULL, NULL, NULL, 37, 1),
(12, 'AR-152', 'Elements of urban and territorial analysis II', 'Eléments d''analyses urbaines et territoriales II', 'http://edu.epfl.ch/coursebook/en/elements-of-urban-and-territorial-analysis-ii-AR-152', 'http://edu.epfl.ch/coursebook/fr/elements-d-analyses-urbaines-et-territoriales-ii-AR-152', 'Ce cours est la continuation du cours "Eléments d''analyses urbaines et territoriales I". Il se concentre sur la forme, les usages et significations des espaces publics, comme caractéristiques essentielles d''une structure urbaine particulière.', NULL, NULL, NULL, NULL, NULL, 37, 1),
(13, 'AR-111', 'Graphic figuration I', 'Figuration graphique I', 'http://edu.epfl.ch/coursebook/en/graphic-figuration-i-AR-111', 'http://edu.epfl.ch/coursebook/fr/figuration-graphique-i-AR-111', 'Initiation au dessin d''architecture à travers ses diverses procédures et codes graphiques, avec le crayon et le papier comme bases.', NULL, NULL, NULL, NULL, NULL, 38, 1),
(14, 'AR-112', 'Graphic figuration II', 'Figuration graphique II', 'http://edu.epfl.ch/coursebook/en/graphic-figuration-ii-AR-112', 'http://edu.epfl.ch/coursebook/fr/figuration-graphique-ii-AR-112', 'A partir des acquisitions de AR-111 Figuration graphique I, application des procédures et effets graphiques d''architecture les plus appropriés à l''expression de caractéristiques spatiales données.', NULL, NULL, NULL, NULL, NULL, 38, 1),
(15, 'CIVIL-122', 'Structures I', 'Structures I', 'http://edu.epfl.ch/coursebook/en/structures-i-CIVIL-122', 'http://edu.epfl.ch/coursebook/fr/structures-i-CIVIL-122', 'Suivi de Structures II, avec lequel il forme un cours annuel, le cours Structures I présente les bases du comportement des structures, de la détermination des efforts qui y agissent et les principes de leur dimensionnement. Le cours est basé sur la résolution des efforts par la statique graphique.', NULL, NULL, NULL, NULL, NULL, NULL, 39, 4),
(16, 'CIVIL-123', 'Structures II', 'Structures II', 'http://edu.epfl.ch/coursebook/en/structures-ii-CIVIL-123', 'http://edu.epfl.ch/coursebook/fr/structures-ii-CIVIL-123', 'Le cours permet de comprendre le fonctionnement, déterminer les efforts et de dimensionner les structures en treillis, en poutre, en dalle et en cadre. Le cours se base sur la résolution des efforts par la statique graphique.', NULL, NULL, NULL, NULL, NULL, NULL, 39, 4),
(17, 'AR-101', 'Studio BA1', 'Théorie et critique du projet BA1', 'http://edu.epfl.ch/coursebook/en/studio-ba1-AR-101', 'http://edu.epfl.ch/coursebook/fr/theorie-et-critique-du-projet-ba1-AR-101', NULL, 1.5, NULL, 1, 2, 4, 40, 1),
(18, 'AR-102', 'Studio BA2', 'Théorie et critique du projet BA2', 'http://edu.epfl.ch/coursebook/en/studio-ba2-AR-102', 'http://edu.epfl.ch/coursebook/fr/theorie-et-critique-du-projet-ba2-AR-102', 'Le 2ème semestre propose une condition de laboratoire dans laquelle les étudiants affinent les outils de la recherche architecturale et de la maîtrise artisanale dans le but de réaliser un projet architectural qui traite d''espace, de construction et de programme dans un discours architectonique.', NULL, NULL, NULL, NULL, NULL, NULL, 40, 1),
(19, 'AR-121', 'Theory of architecture I', 'Théorie de l''architecture I', 'http://edu.epfl.ch/coursebook/en/theory-of-architecture-i-AR-121', 'http://edu.epfl.ch/coursebook/fr/theorie-de-l-architecture-i-AR-121', 'Offrir à l''étudiant une vision élargie des problèmes relatifs aux théories architecturales, avec une attention particulière portée aux principes de composition et de conception architecturale.', NULL, NULL, NULL, NULL, NULL, NULL, 41, 1),
(20, 'AR-122', 'Theory of architecture II', 'Théorie de l''architecture II', 'http://edu.epfl.ch/coursebook/en/theory-of-architecture-ii-AR-122', 'http://edu.epfl.ch/coursebook/fr/theorie-de-l-architecture-ii-AR-122', 'Voir AR-121 Théorie de l''architecture I.', NULL, NULL, NULL, NULL, NULL, 41, 1),
(21, 'MATH-101(g)', 'Analysis I', 'Analyse I', 'http://edu.epfl.ch/coursebook/en/analysis-i-MATH-101-G', 'http://edu.epfl.ch/coursebook/fr/analyse-i-MATH-101-G', 'Étudier les concepts fondamentaux d''analyse et le calcul différentiel et intégral des fonctions réelles d''une variable.', NULL, NULL, NULL, NULL, NULL, 42, 7),
(22, 'MATH-101(en)', 'Analysis I (English)', 'Analyse I (anglais)', 'http://edu.epfl.ch/coursebook/en/analysis-i-english-MATH-101-EN', 'http://edu.epfl.ch/coursebook/fr/analyse-i-anglais-MATH-101-EN', 'We study the fundamental concepts of analysis, calculus and the integral of real-valued functions of a real variable.', NULL, NULL, NULL, NULL, NULL, NULL, 14, 7),
(23, 'MATH-101(de)', 'Analysis I (German)', 'Analyse I (allemand)', 'http://edu.epfl.ch/coursebook/en/analysis-i-german-MATH-101-DE', 'http://edu.epfl.ch/coursebook/fr/analyse-i-allemand-MATH-101-DE', 'Étudier les concepts fondamentaux d''analyse et le calcul différentiel et intégral des fonctions réelles d''une variable.', NULL, NULL, NULL, NULL, NULL, 7, 7),
(24, 'MATH-106(g)', 'Analysis II', 'Analyse II', 'http://edu.epfl.ch/coursebook/en/analysis-ii-MATH-106-G', 'http://edu.epfl.ch/coursebook/fr/analyse-ii-MATH-106-G', 'Étudier les concepts fondamentaux d''analyse et le calcul différentiel et intégral des fonctions réelles de plusieurs\nvariables.', NULL, NULL, NULL, NULL, NULL, 42, 7),
(25, 'MATH-106(en)', 'Analysis II (English)', 'Analyse II (anglais)', 'http://edu.epfl.ch/coursebook/en/analysis-ii-english-MATH-106-EN', 'http://edu.epfl.ch/coursebook/fr/analyse-ii-anglais-MATH-106-EN', 'The course studies fundamental concepts of analysis and the calculus of functions of several variables.', NULL, NULL, NULL, NULL, NULL, NULL, 22, 7),
(26, 'MATH-106(de)', 'Analysis II (German)', 'Analyse II (allemand)', 'http://edu.epfl.ch/coursebook/en/analysis-ii-german-MATH-106-DE', 'http://edu.epfl.ch/coursebook/fr/analyse-ii-allemand-MATH-106-DE', 'Es werden die Grundlagen der Analysis sowie der Differential- und Integralrechnung von Funktionen mehrerer reeller Veränderlicher erarbeitet.', NULL, NULL, NULL, NULL, NULL, 43, 7),
(27, 'CH-102', 'Atoms, ions, molecules and functions', 'Atomes, ions, molécules et fonctions', 'http://edu.epfl.ch/coursebook/en/atoms-ions-molecules-and-functions-CH-102', 'http://edu.epfl.ch/coursebook/fr/atomes-ions-molecules-et-fonctions-CH-102', 'Ce cours introduit les premières notions de chimie quantique permettant de comprendre les propriétés des électrons dans un atome. Les facteurs déterminant la formation d''une liaison chimique et d''une molécule sont ensuite abordés. Finalement, les concepts sont appliqués aux molécules organiques.', NULL, NULL, NULL, NULL, NULL, 44, 2),
(28, 'CH-101', 'Chemical equilibria and reactivity', 'Equilibres et réactivité chimiques', 'http://edu.epfl.ch/coursebook/en/chemical-equilibria-and-reactivity-CH-101', 'http://edu.epfl.ch/coursebook/fr/equilibres-et-reactivite-chimiques-CH-101', 'Cet enseignement vise l''établissement de bases solides en chimie quantitative. Il introduit en particulier les bases  de la thermodynamique régissant les transformations physiques de la matière et les équilibres chimiques. Les premières notions d''électrochimie et de cinétique y sont présentées.', NULL, NULL, NULL, NULL, NULL, 45, 2),
(29, 'PHYS-106(en)', 'General physics (English) II', 'Physique générale II (anglais)', 'http://edu.epfl.ch/coursebook/en/general-physics-english-ii-PHYS-106-EN', 'http://edu.epfl.ch/coursebook/fr/physique-generale-ii-anglais-PHYS-106-EN', 'Give the student the basic notions that will allow him or her to have a better understanding of physical phenomena, such as the mechanic of point masses. Acquire the capacity to analyse quantitatively the consequences of these effects with appropriate theoretical tools.', NULL, NULL, NULL, NULL, NULL, 46, 10),
(30, 'PHYS-101(i)', 'General physics I', 'Physique générale I', 'http://edu.epfl.ch/coursebook/en/general-physics-i-PHYS-101-I', 'http://edu.epfl.ch/coursebook/fr/physique-generale-i-PHYS-101-I', 'Le but du cours de physique générale est de donner à l''étudiant les notions de base nécessaires à la compréhension des phénomènes physiques. L''objectif est atteint lorsque l''étudiant est capable de prévoir quantitativement les conséquences de ces phénomènes avec des outils théoriques appropriés.', NULL, NULL, NULL, NULL, NULL, 47, 10),
(31, 'PHYS-101(en)', 'General physics I (English)', 'Physique générale I (anglais)', 'http://edu.epfl.ch/coursebook/en/general-physics-i-english-PHYS-101-EN', 'http://edu.epfl.ch/coursebook/fr/physique-generale-i-anglais-PHYS-101-EN', 'Give the student the basic notions that will allow him or her to have a better understanding of physical phenomena, such as the mechanic of point masses and solid bodies. Acquire the capacity to analyse quantitatively the consequences of these effects with appropriate theoretical tools.', NULL, NULL, NULL, NULL, NULL, 48, 10),
(32, 'PHYS-101(de)', 'General physics I(German)', 'Physique générale I (allemand)', 'http://edu.epfl.ch//coursebook/en/general-physics-i-german-PHYS-101-DE', 'http://edu.epfl.ch//coursebook/fr/physique-generale-i-allemand-PHYS-101-DE', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0),
(33, 'PHYS-106(i)', 'General physics II', 'Physique générale II', 'http://edu.epfl.ch/coursebook/en/general-physics-ii-PHYS-106-I', 'http://edu.epfl.ch/coursebook/fr/physique-generale-ii-PHYS-106-I', 'Le but du cours de Physique générale est de donner à l''étudiant les notions de base nécessaires à la compréhension des phénomènes physiques. L''objectif est atteint lorsque l''étudiant est capable de prévoir quantitativement les conséquences de ces phénomènes avec des outils théoriques appropriés.', NULL, NULL, NULL, NULL, NULL, 49, 10),
(34, 'PHYS-106(de)', 'General physics II(German)', 'Physique générale II (allemand)', 'http://edu.epfl.ch//coursebook/en/general-physics-ii-german-PHYS-106-DE', 'http://edu.epfl.ch//coursebook/fr/physique-generale-ii-allemand-PHYS-106-DE', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0),
(35, 'MATH-111(g)', 'Linear Algebra', 'Algèbre linéaire', 'http://edu.epfl.ch/coursebook/en/linear-algebra-MATH-111-G', 'http://edu.epfl.ch/coursebook/fr/algebre-lineaire-MATH-111-G', 'L''objectif du cours est d''introduire les notions de base de l''algèbre linéaire et ses applications.', NULL, NULL, NULL, NULL, NULL, 42, 7),
(36, 'MATH-111(en)', 'Linear algebra (english)', 'Algèbre linéaire (anglais)', 'http://edu.epfl.ch/coursebook/en/linear-algebra-english-MATH-111-EN', 'http://edu.epfl.ch/coursebook/fr/algebre-lineaire-anglais-MATH-111-EN', 'The purpose of the course is to introduce the basic notions of linear algebra and its applications.', NULL, NULL, NULL, NULL, NULL, NULL, 11, 7),
(37, 'MATH-111(de)', 'Linear algebra (german)', 'Algèbre linéaire (allemand)', 'http://edu.epfl.ch/coursebook/en/linear-algebra-german-MATH-111-DE', 'http://edu.epfl.ch/coursebook/fr/algebre-lineaire-allemand-MATH-111-DE', 'Das Ziel des Kurses ist es, die grundlegenden Konzepte der linearen Algebra und ihre Anwendungen vorzustellen.', NULL, NULL, NULL, NULL, NULL, NULL, 50, 7),
(38, 'CH-104', 'Organic functions and reactions I', 'Fonctions et réactions organiques I', 'http://edu.epfl.ch/coursebook/en/organic-functions-and-reactions-i-CH-104', 'http://edu.epfl.ch/coursebook/fr/fonctions-et-reactions-organiques-i-CH-104', 'Acquisition des notions fondamentales liées à la réactivité des molécules organiques', NULL, NULL, NULL, NULL, NULL, 51, 2),
(39, 'CH-108(a)', 'Chemistry Laboratory Work I', 'Chimie TP I', 'http://edu.epfl.ch/coursebook/en/chemistry-laboratory-work-i-CH-108-A', 'http://edu.epfl.ch/coursebook/fr/chimie-tp-i-CH-108-A', 'Familiariser l''étudiant avec le travail au laboratoire. Travailler de façon quantitative et/ou qualitative. \nTP réalisés en relation avec les cours de chimie de 1ere année et complémentaires avec le contenu des enseignements TP2 dispensés au semestre suivant', NULL, NULL, NULL, NULL, NULL, 52, 2),
(40, 'CH-109(a)', 'Chemistry Laboratory Work II', 'Chimie TP II', 'http://edu.epfl.ch/coursebook/en/chemistry-laboratory-work-ii-CH-109-A', 'http://edu.epfl.ch/coursebook/fr/chimie-tp-ii-CH-109-A', 'Familiariser l''étudiant aux principes et à la rigueur de l''analyse quantitative.\nIntroduction aux analyses quantitatives classiques. Apprendre à effectuer un travail quantitatif. \nExposer les principes généraux de la chimie analytique quantitative.', NULL, NULL, NULL, NULL, NULL, 44, 2),
(41, 'CS-110(g)', 'Information, Computation, Communication', 'Information, calcul, communication', 'http://edu.epfl.ch/coursebook/en/information-computation-communication-CS-110-G', 'http://edu.epfl.ch/coursebook/fr/information-calcul-communication-CS-110-G', 'Le cours comporte trois modules. Le module 1 aborde la notion d''algorithme et de représentation de l''information. Le module 2 est consacré à l''échantillonnage d''un signal et à la compression de données. Le module 3 aborde des aspects liés aux systèmes: ordinateur, mémoire, communication, sécurité.', NULL, NULL, NULL, NULL, NULL, NULL, 53, 6),
(42, 'CS-111(f)', 'Programming', 'Programmation', 'http://edu.epfl.ch/coursebook/en/programming-CS-111-F', 'http://edu.epfl.ch/coursebook/fr/programmation-CS-111-F', 'Ce cours introduit les principes de base de la programmation, avec un accent sur le language C. Une introduction à l''environnement Unix sera également donnée.', NULL, NULL, NULL, NULL, NULL, 54, 6),
(43, 'CH-160(a)', 'Advanced general chemistry', 'Chimie générale avancée', 'http://edu.epfl.ch/coursebook/en/advanced-general-chemistry-CH-160-A', 'http://edu.epfl.ch/coursebook/fr/chimie-generale-avancee-CH-160-A', 'Cet enseignement vise l''acquisition des notions essentielles relatives à la structure de la matière, aux équilibres et à la réactivité chimiques. Le cours et les exercices fournissent la méthodologie permettant de résoudre par le raisonnement et le calcul des problèmes inédits de chimie générale.', NULL, NULL, NULL, NULL, NULL, NULL, 55, 2),
(44, 'MATH-101(a)', 'Analysis I', 'Analyse I', 'http://edu.epfl.ch/coursebook/en/analysis-i-MATH-101-A', 'http://edu.epfl.ch/coursebook/fr/analyse-i-MATH-101-A', 'Étudier les concepts fondamentaux d''analyse et le calcul différentiel et intégral des fonctions réelles d''une variable.', NULL, NULL, NULL, NULL, NULL, 10, 7),
(45, 'MATH-106(a)', 'Analysis II', 'Analyse II', 'http://edu.epfl.ch/coursebook/en/analysis-ii-MATH-106-A', 'http://edu.epfl.ch/coursebook/fr/analyse-ii-MATH-106-A', 'Étudier les concepts fondamentaux d''analyse et le calcul différentiel et intégral des fonctions réelles de plusieurs\nvariables', NULL, NULL, NULL, NULL, NULL, 15, 7),
(46, 'PHYS-101(a)', 'General physics I', 'Physique générale I', 'http://edu.epfl.ch/coursebook/en/general-physics-i-PHYS-101-A', 'http://edu.epfl.ch/coursebook/fr/physique-generale-i-PHYS-101-A', 'Le but du cours de physique générale est de donner à l''étudiant les notions de base nécessaires à la compréhension des phénomènes physiques. L''objectif est atteint lorsque l''étudiant est capable de prévoir quantitativement les conséquences de ces phénomènes avec des outils théoriques appropriés.', NULL, NULL, NULL, NULL, NULL, 56, 10),
(47, 'PHYS-106(a)', 'General physics II', 'Physique générale II', 'http://edu.epfl.ch/coursebook/en/general-physics-ii-PHYS-106-A', 'http://edu.epfl.ch/coursebook/fr/physique-generale-ii-PHYS-106-A', 'Le but du cours de Physique générale est de donner à l''étudiant les notions de base nécessaires à la compréhension des phénomènes physiques. L''objectif est atteint lorsque l''étudiant est capable de prévoir quantitativement les conséquences de ces phénomènes avec des outils théoriques appropriés', NULL, NULL, NULL, NULL, NULL, 57, 10),
(48, 'CIVIL-103', 'Geology', 'Géologie', 'http://edu.epfl.ch/coursebook/en/geology-CIVIL-103', 'http://edu.epfl.ch/coursebook/fr/geologie-CIVIL-103', 'Les ingénieurs civils et en environnement exercent leurs activités en constante interaction avec l''environnement Terre. Le cours de géologie donne aux étudiants une culture universitaire en géosciences et les bases nécessaires à une ingénierie bien intégrée dans le contexte de notre planète.', NULL, NULL, NULL, NULL, NULL, 58, 4),
(49, 'MATH-123(b)', 'Geometry', 'Géométrie', 'http://edu.epfl.ch/coursebook/en/geometry-MATH-123-B', 'http://edu.epfl.ch/coursebook/fr/geometrie-MATH-123-B', 'Il s''agit d''un cours standard d''introduction à la géométrie des courbes et des surfaces.', NULL, NULL, NULL, NULL, NULL, NULL, 59, 7),
(50, 'MATH-111(a)', 'Linear Algebra', 'Algèbre linéaire', 'http://edu.epfl.ch/coursebook/en/linear-algebra-MATH-111-A', 'http://edu.epfl.ch/coursebook/fr/algebre-lineaire-MATH-111-A', 'L''objectif du cours est d''introduire les notions de base de l''algèbre linéaire et ses applications.', NULL, NULL, NULL, NULL, NULL, 43, 7),
(51, 'MSE-170', 'Materials I', 'Matériaux I', 'http://edu.epfl.ch/coursebook/en/materials-i-MSE-170', 'http://edu.epfl.ch/coursebook/fr/materiaux-i-MSE-170', 'Une introduction à la science des matériaux appliquée aux matériaux de construction courants, en particulier le béton et les métaux. Description de leur fabrication et de leur comportement quant à leur utilisation en construction.', NULL, NULL, NULL, NULL, NULL, 60, 9),
(52, 'CIVIL-124', 'Statics I', 'Statique I', 'http://edu.epfl.ch/coursebook/en/statics-i-CIVIL-124', 'http://edu.epfl.ch/coursebook/fr/statique-i-CIVIL-124', 'Développer une compréhension des modèles statiques. Étude du jeu des forces dans les constructions isostatiques.', NULL, NULL, NULL, NULL, NULL, 61, 4),
(53, 'ENV-140', 'Fundamentals of geomatics', 'Eléments de géomatique', 'http://edu.epfl.ch/coursebook/en/fundamentals-of-geomatics-ENV-140', 'http://edu.epfl.ch/coursebook/fr/elements-de-geomatique-ENV-140', 'Bases de la géomatique pour les ingénieurs civil et en environnement. Présentation des méthodes d''acquisition, de gestion et de représentation des géodonnées. Apprentissage et conduite d''expériences et de calculs de topométrie terrestre.', NULL, NULL, NULL, NULL, NULL, 62, 12),
(54, 'CS-110(a)', 'Information, Computation, Communication', 'Information, calcul, communication', 'http://edu.epfl.ch/coursebook/en/information-computation-communication-CS-110-A', 'http://edu.epfl.ch/coursebook/fr/information-calcul-communication-CS-110-A', 'Le cours comporte trois modules. Le module 1 aborde la notion d''algorithme et de représentation de l''information. Le module 2 est consacré à l''échantillonnage d''un signal et à la compression de données. Le module 3 aborde des aspects liés aux systèmes: ordinateur, mémoire, communication, sécurité.', NULL, NULL, NULL, NULL, NULL, 63, 6),
(55, 'CS-111(a)', 'Programming', 'Programmation', 'http://edu.epfl.ch/coursebook/en/programming-CS-111-A', 'http://edu.epfl.ch/coursebook/fr/programmation-CS-111-A', 'Ce cours présente une introduction à la programmation impérative avec le langage Java. Vous apprenez à réaliser de petits programmes simples dans Eclipse. Le cours est axé sur la pratique, les exercices se font en salle d''ordinateurs.', NULL, NULL, NULL, NULL, NULL, 64, 6),
(56, 'MATH-101(e)', 'Analysis I', 'Analyse I', 'http://edu.epfl.ch/coursebook/en/analysis-i-MATH-101-E', 'http://edu.epfl.ch/coursebook/fr/analyse-i-MATH-101-E', 'Étudier les concepts fondamentaux d''analyse et le calcul différentiel et intégral des fonctions réelles d''une variable.', NULL, NULL, NULL, NULL, NULL, NULL, 65, 7),
(57, 'MATH-106(e)', 'Analysis II', 'Analyse II', 'http://edu.epfl.ch/coursebook/en/analysis-ii-MATH-106-E', 'http://edu.epfl.ch/coursebook/fr/analyse-ii-MATH-106-E', 'Étudier les concepts fondamentaux d''analyse et le calcul différentiel et intégral des fonctions réelles de plusieurs\nvariables.', NULL, NULL, NULL, NULL, NULL, NULL, 65, 7),
(58, 'CS-150', 'Discrete structures', 'Discrete structures', 'http://edu.epfl.ch/coursebook/en/discrete-structures-CS-150', 'http://edu.epfl.ch/coursebook/fr/discrete-structures-CS-150', 'Discrete mathematics is a discipline with applications to almost every area of study, and provides an indispensable set of tools to computer science in particular. This course introduces students to topics as diverse as mathematical reasoning, combinatorics, discrete structures, algorithmic thinking', NULL, NULL, NULL, NULL, NULL, NULL, 66, 11),
(59, 'PHYS-101(g)', 'General physics I', 'Physique générale I', 'http://edu.epfl.ch/coursebook/en/general-physics-i-PHYS-101-G', 'http://edu.epfl.ch/coursebook/fr/physique-generale-i-PHYS-101-G', 'Le but du cours de physique générale est de donner à l''étudiant les notions de base nécessaires à la compréhension des phénomènes physiques. L''objectif est atteint lorsque l''étudiant est capable de prévoir quantitativement les conséquences de ces phénomènes avec des outils théoriques appropriés.', NULL, NULL, NULL, NULL, NULL, NULL, 67, 10),
(60, 'COM-101', 'Information sciences', 'Sciences de l''information', 'http://edu.epfl.ch/coursebook/en/information-sciences-COM-101', 'http://edu.epfl.ch/coursebook/fr/sciences-de-l-information-COM-101', 'Les fichiers échangés sur Internet et stockés sur les disques durs contiennent de l''information qui deviendra finalement du texte, des images ou des sons. Comment cette information est-elle mesurée et comprimée ? Sécurisée ? Protégée ? Ce sont les trois questions auxquelles ce cours répond.', NULL, NULL, NULL, NULL, NULL, NULL, 68, 11),
(61, 'MATH-111(e)', 'Linear Algebra', 'Algèbre linéaire', 'http://edu.epfl.ch/coursebook/en/linear-algebra-MATH-111-E', 'http://edu.epfl.ch/coursebook/fr/algebre-lineaire-MATH-111-E', 'L''objectif du cours est d''introduire les notions de base de l''algèbre linéaire et ses applications.', NULL, NULL, NULL, NULL, NULL, NULL, 69, 7),
(62, 'CS-173', 'Digital systems design', 'Conception de systèmes numériques', 'http://edu.epfl.ch/coursebook/en/digital-systems-design-CS-173', 'http://edu.epfl.ch/coursebook/fr/conception-de-systemes-numeriques-CS-173', 'Les étudiants se familiarisent avec les composants numériques de base des systèmes de traitement de l''information, et apprennent à utiliser des méthodes modernes d''analyse et de synthèse des systèmes combinatoires et séquentiels, à l''aide notamment des langages tels que VHDL.', NULL, NULL, NULL, NULL, NULL, NULL, 70, 6),
(63, 'CS-110(e)', 'Information, computation, communication', 'Information, computation, communication', 'http://edu.epfl.ch/coursebook/en/information-computation-communication-CS-110-E', 'http://edu.epfl.ch/coursebook/fr/information-computation-communication-CS-110-E', 'The course is structured into 3 modules. Module 1 present the concepts of algorithms and representation of information. Module 2 focuses on sampling and compression. Module 3 offer an overview of systems: CPU, memory, communication and security.', NULL, NULL, NULL, NULL, NULL, NULL, 71, 6),
