(68, 'EE-100', 'Electrical engineering science and technology (Autumn)', 'Sciences et technologies de l''électricité (automne)', 'http://edu.epfl.ch/coursebook/en/electrical-engineering-science-and-technology-autumn-EE-100', 'http://edu.epfl.ch/coursebook/fr/sciences-et-technologies-de-l-electricite-automne-EE-100', 'Ce cours propose une introduction aux sciences et techniques de l''électricité en mettant l''accent sur les composants des circuits électriques.', NULL, NULL, NULL, NULL, NULL, 75, 3),
(69, 'CH-161(b)', 'General chemistry', 'Chimie générale', 'http://edu.epfl.ch/coursebook/en/general-chemistry-CH-161-B', 'http://edu.epfl.ch/coursebook/fr/chimie-generale-CH-161-B', 'Cet enseignement vise l''acquisition des notions essentielles relatives à la structure de la matière, aux équilibres et à la réactivité chimiques. Le cours et les exercices fournissent la méthodologie permettant de résoudre par le raisonnement et le calcul des problèmes inédits de chimie générale.', NULL, NULL, NULL, NULL, NULL, 76, 2),
(70, 'CH-161(en)', 'General chemistry (English)', 'Chimie générale (anglais)', 'http://edu.epfl.ch/coursebook/en/general-chemistry-english-CH-161-EN', 'http://edu.epfl.ch/coursebook/fr/chimie-generale-anglais-CH-161-EN', 'This course aims at the acquisition of essential notions on the structure of matter, chemical equilibria and reactivity. Theoretical teaching and exercise provide the methodology to analyze and solve by reasoning and calculation novel problems of general chemistry.', NULL, NULL, NULL, NULL, NULL, 77, 2),
