(87, 'MATH-131', 'Probabilities and statistics', 'Probabilités et statistique', 'http://edu.epfl.ch/coursebook/en/probabilities-and-statistics-MATH-131', 'http://edu.epfl.ch/coursebook/fr/probabilites-et-statistique-MATH-131', 'Le cours présente les notions de base de la théorie des probabilités et de l''inférence statistique, en mettant l''accent sur les concepts et méthodes principales.', NULL, NULL, NULL, NULL, NULL, 89, 7),
(88, 'ENV-168', 'Environmental monitoring laboratory', 'TP de monitoring environnemental', 'http://edu.epfl.ch/coursebook/en/environmental-monitoring-laboratory-ENV-168', 'http://edu.epfl.ch/coursebook/fr/tp-de-monitoring-environnemental-ENV-168', 'L''étudiant acquerra les connaissances et qualifications suivantes: principes de base de la mesure; construire et utiliser des instruments scientifiques (pH-mètre, spectrophotomètre, thermocouple) dans des analyses environnementales; estimer des erreurs de mesure; travailler avec des microorganismes', NULL, NULL, NULL, NULL, NULL, 90, 12),
(89, 'ENV-167', 'Introduction to environmental engineering', 'Introduction to environmental engineering', 'http://edu.epfl.ch/coursebook/en/introduction-to-environmental-engineering-ENV-167', 'http://edu.epfl.ch/coursebook/fr/introduction-to-environmental-engineering-ENV-167', 'This introduction to Enviromental Engineering is meant to show the students how upcoming courses in mathematics, physics, chemistry, biology and other areas will be used to gain a scientific understanding of environmental problems and then help to solve them.', NULL, NULL, NULL, NULL, NULL, 91, 12),
